var mixin = {
  data: function () {
    return {
      mStr: 'string from mixin - dll',
      mObj:{}
    }
  },
  // 如果这个属性是在mounted事件添加的话, 会导致无法显示, 不过这个与是否打包无关
  // element源码中, 相关的属性是通过watch触发的: https://github.com/ElemeFE/element/blob/dev/src/utils/vue-popper.js#L72
  created () {
    this.mObj.moProp = 'object prop from mixin -dll'
  }
}
export default {
  render: function (h) {
    return  h('div',{},[
      this.str, h('br'), 
      this.mStr, h('br'),
      this.mObj.moProp
    ])
  },
  name: 'MixinTest',
  mixins: [mixin],
  data: function () {
    return {
      str: 'string from data - dll',
      mObj:{}
    }
  }
}